import React, { useState } from 'react';
import './App.css';

const createKey = () => {
  const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
  let result = '';
  const charactersLength = characters.length;

  for (let i = 0; i < 8; i++) {
    result += characters.charAt(Math.floor(Math.random() * charactersLength));
  }

  return result;
};

const AgentRow = (props) => {
  return (
    <div style={{width: '100%', display: 'grid', gridTemplateColumns: 'repeat(5,1fr)', marginBottom: '5px'}}>
      <input value="AGENT" disabled style={{ height: '50px', textAlign: 'center' }} />
      <textarea placeholder="Role" value={props.role} onChange={(e) => props.updateAgent(props.id, 'role', e.target.value)} style={{ height: '50px' }} />
      <textarea placeholder="Goal" value={props.goal} onChange={(e) => props.updateAgent(props.id, 'goal', e.target.value)} style={{ height: '50px' }} />
      <textarea placeholder="Backstory" value={props.backstory} onChange={(e) => props.updateAgent(props.id, 'backstory', e.target.value)} style={{ height: '50px' }} />
      <button onClick={() => props.deleteAgent(props.id)}>Delete Agent</button>
    </div>
  );
};

const TaskRow = (props) => {
  return (
    <div style={{width: '100%', display: 'grid', gridTemplateColumns: '1fr 1fr 2fr 1fr', marginBottom: '5px'}}>
      <input value="TASK" disabled style={{ height: '50px', textAlign: 'center' }} />
      <textarea placeholder="Role" value={props.role} onChange={(e) => props.updateTask(props.id, 'role', e.target.value)} style={{ height: '50px' }} />
      <textarea placeholder="Description" value={props.goal} onChange={(e) => props.updateTask(props.id, 'description', e.target.value)} style={{ height: '50px' }} />
      <button onClick={() => props.deleteTask(props.id)}>Delete Task</button>
    </div>
  );
};

const App = () => {
  const [input, setInput] = useState("");
  const [agents, setAgents] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [data, setData] = useState("");

  const fetchGeneratedData = () => {
    fetch('http://127.0.0.1:5000/api/generate', {
      method: "POST",
      headers: {
        "Content-Type": 'application/json',
      },
      body: JSON.stringify({ prompt: input })
    })
    .then(response => response.json())
    .then(response => {
      // Assuming response contains fields 'agents' and 'tasks'
      setAgents(response.agents.map(agent => ({ ...agent, id: createKey() })));
      setTasks(response.tasks.map(task => ({ ...task, id: createKey() })));
    })
    .catch(err => console.error("Error: ", err));
  };

  const kickoffCrew = () => {
    const allData = {
      agents,
      tasks
    };

    setData("Kickoff in progress...");
    fetch('http://127.0.0.1:5000/api/execute', {
      method: "POST",
      headers: {
        "Content-Type": 'application/json',
      },
      body: JSON.stringify(allData)
    })
    .then(response => response.text())
    .then(response => setData(response))
    .catch(err => setData(`There was an error: ${err}`));
  };


  const addAgent = () => {
    setAgents([...agents, { id: createKey() }]);
  };

  const addTask = () => {
    setTasks([...tasks, { id: createKey() }]);
  };

  const deleteAgent = (agentId) => {
    setAgents((currentAgents) => currentAgents.filter(agent => agent.id !== agentId));
  };

  const deleteTask = (taskId) => {
    setTasks((currentTasks) => currentTasks.filter(task => task.id !== taskId));
  };

  const updateAgent = (id, prop, value) => {
    setAgents(agents.map((agent) => {
      if (agent.id === id) {
        return { ...agent, [prop]: value };
      }
      return agent;
    }));
  };

  const updateTask = (id, prop, value) => {
    setTasks(tasks.map((task) => {
      if (task.id === id) {
        return { ...task, [prop]: value };
      }
      return task;
    }));
  };

  const submitData = () => {
    const allData = {
      agents,
      tasks,
      prompt: input
    };

    setData("Loading...");
    fetch('http://127.0.0.1:5000/api/generate', {
      method: "POST",
      headers: {
        "Content-Type": 'application/json',
      },
      body: JSON.stringify(allData)
    })
    .then(response => response.text())
    .then(response => setData(response))
    .catch(err => setData(`There was an error: ${err}`));
  };

  return (
    <div className="App">
      <h2>TaskForce AI</h2>
      <textarea 
        placeholder="Type your prompt here..." 
        rows={4} 
        value={input} 
        onChange={(e) => setInput(e.target.value)}
        style={{ width: '100%', padding: '10px', margin: '10px 0' }}
      />
      <button onClick={fetchGeneratedData}>Generate Agents and Tasks</button>
      {agents.map((agent) => (
        <AgentRow 
          role={agent.role}
          goal={agent.goal}
          backstory={agent.backstory}
          id={agent.id}
          key={agent.id}
          updateAgent={updateAgent}
          deleteAgent={deleteAgent}
        />
      ))}
      <button onClick={addAgent}>Add Agent +</button>
      {tasks.map((task) => (
        <TaskRow 
          role={task.role}
          description={task.description}
          id={task.id}
          key={task.id}
          updateTask={updateTask}
          deleteTask={deleteTask}
        />
      ))}
      <button onClick={addTask}>Add Task +</button>
      <button onClick={kickoffCrew}>Kickoff Crew</button>
      <p>{data}</p>
    </div>
  );
};

export default App;
