import React, { useState } from 'react';
import './App.css';

const createKey = () => {
  const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
  let result = '';
  const charactersLength = characters.length;

  for (let i = 0; i < 8; i++) {
    result += characters.charAt(Math.floor(Math.random() * charactersLength));
  }

  return result;
};

const AgentRow = ({ agent, updateAgent, deleteAgent }) => {
  return (
    <div className="agent-row">
      <input value="AGENT" disabled />
      <textarea placeholder="Role" value={agent.role} onChange={(e) => updateAgent(agent.role, 'role', e.target.value)} />
      <textarea placeholder="Goal" value={agent.goal} onChange={(e) => updateAgent(agent.role, 'goal', e.target.value)} />
      <textarea placeholder="Backstory" value={agent.backstory} onChange={(e) => updateAgent(agent.role, 'backstory', e.target.value)} />
      <button onClick={() => deleteAgent(agent.role)}>Delete Agent</button>
    </div>
  );
};

const TaskRow = ({ task, updateTask, deleteTask }) => {
  return (
    <div className="task-row">
      <input value="TASK" disabled />
      <textarea placeholder="Role" value={task.role} onChange={(e) => updateTask(task.role, 'role', e.target.value)} />
      <textarea placeholder="Description" value={task.description} onChange={(e) => updateTask(task.role, 'description', e.target.value)} />
      <button onClick={() => deleteTask(task.role)}>Delete Task</button>
    </div>
  );
};

const App = () => {
  const [input, setInput] = useState("");
  const [agents, setAgents] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [data, setData] = useState("");

  const fetchGeneratedData = () => {
    fetch('http://127.0.0.1:5000/api/generate', {
      method: "POST",
      headers: {
        "Content-Type": 'application/json',
      },
      body: JSON.stringify({ prompt: input })
    })
    .then(response => response.json())
    .then(response => {
      // Assuming response contains fields 'agents' and 'tasks'
      setAgents(response.agents.map(agent => ({ ...agent, id: createKey() })));
      setTasks(response.tasks.map(task => ({ ...task, id: createKey() })));
    })
    .catch(err => console.error("Error: ", err));
  };

  const kickoffCrew = () => {
    const allData = {
      agents,
      tasks
    };

    setData("Kickoff in progress...");
    fetch('http://127.0.0.1:5000/api/execute', {
      method: "POST",
      headers: {
        "Content-Type": 'application/json',
      },
      body: JSON.stringify(allData)
    })
    .then(response => response.text())
    .then(response => setData(response))
    .catch(err => setData(`There was an error: ${err}`));
  };


  const addAgent = () => {
    setAgents([...agents, { id: createKey() }]);
  };

  const addTask = () => {
    setTasks([...tasks, { id: createKey() }]);
  };

  const deleteAgent = (agentId) => {
    setAgents((currentAgents) => currentAgents.filter(agent => agent.id !== agentId));
  };

  const deleteTask = (taskId) => {
    setTasks((currentTasks) => currentTasks.filter(task => task.id !== taskId));
  };

  const updateAgent = (id, prop, value) => {
    setAgents(agents.map((agent) => {
      if (agent.id === id) {
        return { ...agent, [prop]: value };
      }
      return agent;
    }));
  };

  const updateTask = (id, prop, value) => {
    setTasks(tasks.map((task) => {
      if (task.id === id) {
        return { ...task, [prop]: value };
      }
      return task;
    }));
  };

  const submitData = () => {
    const allData = {
      agents,
      tasks,
      prompt: input
    };

    setData("Loading...");
    fetch('http://127.0.0.1:5000/api/generate', {
      method: "POST",
      headers: {
        "Content-Type": 'application/json',
      },
      body: JSON.stringify(allData)
    })
    .then(response => response.text())
    .then(response => setData(response))
    .catch(err => setData(`There was an error: ${err}`));
  };


  return (
    <div className="App">
      <h2>TaskForce AI</h2>
      <div className="prompt-container">
        <textarea 
          className="prompt-box"
          placeholder="Type your prompt here..." 
          rows={2} 
          value={input} 
          onChange={(e) => setInput(e.target.value)}
        />
        <button className="btn-generate" onClick={fetchGeneratedData}>Generate</button>
      </div>
      <div className="agent-task-container">
        {agents.map((agent, index) => (
          <div key={index} className="agent-row">
            <div>Agent: {agent.role}</div>
            <div>Goal: {agent.goal}</div>
            <div>Backstory: {agent.backstory}</div>
            <button onClick={() => deleteAgent(agent.role)}>Delete Agent</button>
          </div>
        ))}
        <button onClick={addAgent}>Add Agent +</button>
        {tasks.map((task, index) => (
          <div key={index} className="task-row">
            <div>Task for {task.role}</div>
            <div>Description: {task.description}</div>
            <button onClick={() => deleteTask(task.role)}>Delete Task</button>
          </div>
        ))}
        <button onClick={addTask}>Add Task +</button>
      </div>
      <button className="btn-kickoff" onClick={kickoffCrew}>Kickoff Crew</button>
      <div className="response-area">{data}</div>
    </div>
  );  
};

export default App;
