import React, { useState } from 'react';
import './App.css';

function App() {
  const [data, setData] = useState("");
  const [input, setInput] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const examplePrompts = [
    "Analyze the latest trends in artificial intelligence and machine learning.",
    "Provide a summary of recent developments in renewable energy technologies.",
    "Generate a creative story set in a futuristic world where AI governs society.",
    "Outline the steps to develop a mobile app for health and fitness tracking."
  ];

  const submitData = () => {
    setIsLoading(true);
    setData("");
    fetch('http://127.0.0.1:5000/api', {
      method: "POST",
      headers: {
        "Content-Type": 'application/json',
      },
      body: JSON.stringify(input)
    })
    .then(response => response.text())
    .then(response => {
      setData(response);
      setIsLoading(false);
    })
    .catch(err => {
      setData("There was an error processing your request.");
      setIsLoading(false);
    });
  }

  const setExampleInput = (prompt) => {
    setInput(prompt);
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>TaskForces.ai</h1>
      </header>
      <main className="App-main">
        <div className="Example-buttons">
          {examplePrompts.map((prompt, index) => (
            <button key={index} onClick={() => setExampleInput(prompt)}>
              Example {index + 1}
            </button>
          ))}
        </div>
        <textarea 
          className="Input-area"
          placeholder="Enter your prompt here or select an example above..." 
          rows={10} 
          value={input} 
          onChange={e => setInput(e.target.value)}
        />
        <button className="Submit-button" onClick={submitData}>Submit</button>
        {isLoading ? <p>Loading...</p> : <div className="Response-area">{data}</div>}
      </main>
    </div>
  );
}

export default App;
